import React,{Component} from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';

class App extends Component {
  state = {
    response: '', post: '', responseToPost: ''
  };

  componentDidMount()
  {
    this.callApi().then(
        res => this.setState({ response: res.express }))
        .catch(err => console.log(err));
  }
  callApi = async () => {
    const response = await fetch('/api/hello');
    const body = await response.json();
    if (response.status !== 200)
      throw Error(body.message);
    return body;
  };

  handleSubmit =  (e) => {
    e.preventDefault();
    const postfromuser ={
        post:this.state.post
    };
    axios.post('/api/world',{postfromuser}).then(res =>{
        console.log(res);
        this.setState({
            responseToPost:res.data
        });
    });

  };
  render() {
    return (
        <div className="App">
          <header className="App-header">
            <img src={logo} className="App-logo" alt="logo"/>
            <p>Edit <code>src/App.js</code> and save to reload.</p>
            <a className="App-link" href="https://reactjs.org" target="_blank" rel="noopener noreferrer">Learn React</a>
          </header>
          <p>{this.state.response}</p>
          <form onSubmit={this.handleSubmit}>
            <p><strong>Post to Server:</strong></p>
            <input type="text" value={this.state.post} name="post" onChange={e => this.setState({post: e.target.value})}/>
            <button type="submit">Submit</button>
          </form>
          <p>{this.state.responseToPost}</p>
        </div>
    );
  }
}
export default App;
